INSERT INTO `wp_postmeta` (`post_id`, `meta_key`, `meta_value`) VALUES (3186, 'feature-body', '<h4 style="text-align: center;">POWERFUL JETS</h4>\n<p>Limelight<sup>®</sup> Collection spas are designed to feel as good as they look. Comfortable seats feature powerful jets that massage from your shoulders to your toes, providing targeted relief to help you feel your best.</p>\n<h4 style="text-align: center;">THE PERFECT MASSAGE</h4>\n<p>Limelight<sup>®</sup> Collection spas feature a variety of jets ranging from targeted Precision jets, to XL combination jets that soothe larger muscles.</p>\n<ul>\n<li>Combination XL jets provide a broad, robust massage to relieve tension in large areas, and can be adjusted to provide a direct stream or rotating massage</li>\n</ul>\n');
