INSERT INTO `wp_postmeta` (`post_id`, `meta_key`, `meta_value`) VALUES (3222, 'feature-body', '<p>Beautify Your Space</p>\n<p>Designed to delight the senses, each spa features eye-catching finishes, dramatic LED lighting, sleek metallic corners, and distinctive design details. Subtly embossed cabinets evoke the beauty of natural materials with the durability of advanced composites. Choose from a soothing color palate that follows current design trends and complements your indoor and outdoor space.</p>\n<p>A large bartop, contoured seating, and integrated pillows complement the overall design for a unique and on-trend look.</p>\n');
